
var $ = require('jquery');


module.exports = function(Time_ugly) {

    Hours_array = [
        '1 am',
        '2 am',
        '3 am',
        '4 am',
        '5 am',
        '6 am',
        '7 am',
        '8 am',
        '9 am',
        '10 am',
        '11 am',
        '12 pm',
        '1 pm',
        '2 pm',
        '3 pm',
        '4 pm',
        '5 pm',
        '6 pm',
        '7 pm',
        '8 pm',
        '9 pm',
        '10 pm',
        '11 pm',
        '12 am'
    ];

    var Ugly_split = Time_ugly.split(':');
    var Hour_ugly = Ugly_split[0];
    var Minutes_ugly = Ugly_split[1];

    var Hour_pretty = Hours_array[Hour_ugly - 1];

    if (Minutes_ugly == '00') {
        Minutes_ugly = '';
    } else {
        Minutes_ugly = ':' + Minutes_ugly;
    }

    var Hour_Pretty_split = Hour_pretty.split(' ');
    var Time_pretty = Hour_Pretty_split[0] + Minutes_ugly + ' ' + Hour_Pretty_split[1];

    return Time_pretty;

};